func TestFailingSignInput(t *testing.T) {
	wallet, err := newTestWallet()
	if err != nil {
		t.Fatal(err)
	}

	tests := []struct {
		opts SignInputOpts
		err  error
	}{
		{
			opts: SignInputOpts{
				PsetBase64:     "",
				InIndex:        0,
				DerivationPath: "0'/0/0",
			},
			err: ErrNullPset,
		},
		{
			opts: SignInputOpts{
				PsetBase64:     "",
				InIndex:        0,
				DerivationPath: "0'/0/0",
			},
			err: ErrNullInputWitnessUtxo,
		},
		{
			opts: SignInputOpts{
				PsetBase64:     "",
				InIndex:        3,
				DerivationPath: "0'/0/0",
			},
			err: nil,
		},
		{
			opts: SignInputOpts{
				PsetBase64:     "",
				InIndex:        0,
				DerivationPath: "0'/0",
			},
			err: ErrInvalidDerivationPathLength,
		},
	}

	for _, tt := range tests {
		_, err := wallet.SignInput(tt.opts)
		if tt.err != nil {
			assert.Equal(t, tt.err, err)
		} else {
			assert.NotNil(t, err)
		}
	}
}
